css:
.bdr{
    border:2px solid black;
}
.bdr th{
    border:2px solid black;
}
.bdr td{
    border:2px solid black;
}
.line { 
  display: block;
  margin-top: 0.5em;
  margin-bottom: 0.5em;
  margin-left: auto;
  margin-right: auto;
  border-style: inset;
  border-width: 1px;
}

html:
{% set add %} {% endset %}
{%  set add = frappe.get_doc("Dynamic Link", { "link_name":doc.customer, "link_doctype": "Customer","parenttype":"Address"}, "parent") %} 

{% set address_new %} {% endset %}
{% set address_new = frappe.get_doc("Address", {"name": add.parent}) %} 

{% set ad%} {% endset %}
{%  set ad = frappe.get_doc("Dynamic Link", { "link_name":doc.company, "link_doctype": "Company","parenttype":"Address"}, "parent") %} 

{% set new %} {% endset %}
{% set new = frappe.get_doc("Address", {"name": ad.parent,"is_primary_address":1,"is_shipping_address":1}) %} 

{% set tb %} {% endset %}
{%if doc.customer%}
{% set tb = frappe.get_doc("Customer",doc.customer) %}
{% endif %}

{% set cpc %} {% endset %}
{%if doc.customer%}
{% set cpc = frappe.get_doc("Customer",doc.customer) %}
{% endif %}

<table>
    <tr>
        <td>
            {% if letter_head %}
                {{ letter_head }}
            {% endif %}
        </td>
    </tr>
</table>
<h1 style="text-align:right;">
{%if doc.heading %}
    {{ doc.heading }}
{% endif %}
</h1>
<hr class="line">
<div style="width:100%;font-size: 15px;">
    <table style="width:100%;">
  <tr>
    <td style="width:25%;">
      <b style="font-size:15px;">{{ _("Customer Name") }}:</b>&nbsp; <span style="font-size: 18px">
        </span>  
    </td>
    <td style ="width:25%;">
        <b>
         {% if doc.customer %}
            {{ doc.customer }}
        {% endif %}</b>
    </td>
    <td style ="width:25%;">
        <b style="font-size:15px;">{{ _("Date") }}:</b>&nbsp; <span style="font-size: 18px"></span> 
    </td>
    <td style ="width:25%;">
        {% if doc.opening_date %}
            {{ doc.opening_date }}
        {% endif %}
    </td>
  </tr>
  <tr>
       <td style="width:25%;">
        <b style="font-size:15px;" >{{ _("Contact") }}:</b>&nbsp; <span style="font-size: 18px"></span>
    </td>
    <td style="width:25%;"  >
         {% if cpc.customer_primary_contact %}
            {{ cpc.customer_primary_contact }}
        {% endif %}
    </td>

    <td style="width:25%;">
        <b style="font-size:15px;" >{{ _("Company") }}:</b>&nbsp; <span style="font-size: 18px"></span>
    </td>
    <td style ="width:25%;">
        {% if doc.company%}
            {{ doc.company }}
        {% endif %}
    </td>
  </tr>
  <tr>
    <td style="width:25%;">
        <b style="font-size:15px;" >{{ _("Address") }}:</b>&nbsp; <span style="font-size: 18px"></span>
    </td>
    <td style="width:25%;">
        {% if address_new.address_line1 %} 
             {{address_new.address_line1}}, 
        {% endif %}<br>
        {% if address_new.city %} 
            {{address_new.city}} 
        {% endif %}
        {% if address_new.pincode %} 
            {{address_new.pincode}}, 
        {% endif %}
        {% if address_new.gst_state %} 
            {{address_new.gst_state}}, 
        {% endif %}
        {% if address_new.country %} 
            {{address_new.country}}. 
        {% endif %}<br>

        {% if address_new.phone %}
            {{address_new.phone}}
        {% endif %}<br>


         {{ _("Email") }}: {% if address_new.email_id %}
            {{address_new.email_id}}
        {% endif %}<br>
    </td>
    <td style="width:25%;">
         <b style="font-size:15px;" >{{ _("Company Address") }}:</b>&nbsp; <span style="font-size: 18px"></span>
    </td>
     <td style="width:25%;">
        {% if new.address_line1 %} 
             {{new.address_line1}}, 
        {% endif %}<br>
        {% if new.city %} 
            {{new.city}} 
        {% endif %}
        {% if new.pincode %} 
            {{new.pincode}}, 
        {% endif %}
        {% if new.gst_state %} 
            {{new.gst_state}}, 
        {% endif %}
        {% if new.country %} 
            {{new.country}}. 
        {% endif %}<br>

        {% if new.phone %}
            {{new.phone}}
        {% endif %}<br>


         {{ _("Email") }}: {% if new.email_id %}
            {{new.email_id}}
        {% endif %}<br>
    </td>
  </tr>
</table>
<br><br>
<table class="bdr" style="width:100%;"  >
  <tr  style="border-bottom: 1px solid;" >
    <th style="width:10%;">sr</th>
    <th style="width:30%;"> Subject</th>
    <th style="width:20%;">Estimated Hours</th>
    <th style="width:20%;">Rate</th>
    <th style="width:20%;">Amount</th>
  </tr>
  <tr >
    <td>1</td>
    <td>
        {% if doc.subject%}
            {{ doc.subject}}
        {% endif %}   
    </td>
    <td>
        {% if doc.estimated_hours %}
            {{ doc.estimated_hours/3600}}Hour(s)
        {% endif %}
    </td>
    <td>
        {% if doc.estimated_hours %}
        {% set rate = doc.estimated_cost/(doc.estimated_hours/3600)%} 
        {{'%0.2f' % rate|float}}
        {% endif %}
    </td>
     <td>
         <p> {{ doc.get_formatted("estimated_cost")}}</p>
    </td>
  </tr>
 </table>
 <br><br><br>
  <table style="width:100%">
  <tr>
    <td style="width:25%;">
      <b style="font-size:15px;">{{ _("Total Quantity") }}:</b>&nbsp; <span style="font-size: 18px">
        </span>  
    </td>
    <td style ="width:25%;">
        {% if doc.estimated_hours %}
            {{ doc.estimated_hours/3600}}
        {% endif %}
    </td>


    <td style ="width:25%;">
        <b style="font-size:15px;">{{ _("Total") }}:</b>&nbsp; <span style="font-size: 18px"></span> 
    </td>
    <td style ="width:25%;">

        <p> {{ doc.get_formatted("estimated_cost")}}</p>

    </td>
   </tr>
     {% if doc.currency=="INR" %}
   <tr>
       <td></td>
       <td></td>
       <td style="width:25%;">
       <b style="font-size:15px;">{{ _("GST@18.0") }}:</b>&nbsp; <span style="font-size: 18px"></span>
    </td>
    <td style="width:25%;">

       {% set total = doc.grand_total-doc.estimated_cost%}
        ₹ {{'%0.2f' % total|float}}
    </td>
   </tr>
     {% endif %}
   <tr>
       <td></td>
       <td></td>
    <td style="width:25%;">
       <b style="font-size:15px;">{{ _("Grand Total") }}:</b>&nbsp; <span style="font-size: 18px"></span>
    </td>
    <td style="width:25%;">
        {% if doc.currency !="INR" %}
        <p>{{ doc.get_formatted("estimated_cost")}}</p>
        {% endif %}
        {% if doc.currency =="INR" %}
        <p>{{ doc.get_formatted("grand_total")}}</p>
        {% endif %}
    </td>
    </tr>
    <tr>
        <td></td>
        <td></td>
        <td style="width:25%;">
        <b style="font-size:15px;" >{{ _("Rounded Total") }}:</b>&nbsp; <span style="font-size: 18px"></span>
        </td>
    <td style="width:25%;">

          {% if doc.currency !="INR" %}
        <p>{{ doc.get_formatted("estimated_cost")}}</p>
        {% endif %}
        {% if doc.currency =="INR" %}
        <p>{{ doc.get_formatted("grand_total")}}</p>
        {% endif %}

    </td>
    </tr>
    <tr>
        <td></td>
        <td></td>
        <td style="width:25%;">
        <b style="font-size:15px;" >{{ _("In words") }}:</b>&nbsp; <span style="font-size: 18px"></span>
        </td>
        <td style="width:25%;">
       {% if doc.currency !="INR" %}
        <p>{{doc.estimated_cost_in_words}}</p>
        {% endif %}
         {% if doc.currency =="INR" %}
          <p>{{doc.grand__total_cost_in_words}}</p>
        {% endif %}
    </td>
    </tr>
</table>
</div>
<br><br><br><br>
