css:
/* External CSS for index.html */

.print-format{
    margin:0px !important;


}


.print-format td{
    color:black;
    border:1px solid;
    padding:2px !important;
    font-size:12px;
    margin:0px !important;

}

@page {
        size: A6 landscape;

        }

html:
{%set task%}{% endset %}
{%set a={'project':doc.name,'status':["not in",["Cancelled","Hold"]]}%}
{%if doc.flags.print_settings.hide_group_tasks%}
{%if a.update({"is_group":0})%}{% endif %}
{%endif%}
{%if doc.flags.print_settings.hide_com_task%}
{%if a.update({'status':["not in",["Cancelled","Hold","Completed"]]})%}{% endif %}
{% endif %}
{%if doc.flags.print_settings.show_billable_only%}
{%if a.update({'is_billable':1})%}{% endif %}
{%endif%}
{% set task = frappe.get_all('Task',a,['*'],order_by='exp_start_date asc') %}

{%set emp%}{% endset %}
{% if doc.primary_consultant %}
{% set emp = frappe.get_doc('Employee',doc.primary_consultant)%}{% endif %}
{%set lead%}{% endset %}
{% if doc.project_lead %}
{% set lead = frappe.get_doc('Employee',doc.project_lead)%}{% endif %}
 {%if letter_head%}
<tabLe>
    <tr><td>

        {{letter_head}}

    </td></tr>
</tabLe>
{%endif%}

    <table style="width:100%;border:1px solid black;">
        <tr>
           <td style="font-size: 15px; text-align: center;">PLAN SHEET</td>
        </tr>
    </table>
        <table style="width:100%;border:1px solid black;border-bottom:none;">
                <tr><td><b>Project Code </b></td> 
                    <td>{% if doc.name%}{{doc.name}}{% endif %}</td>
              <td><b>Project Name</b></td>
                    <td>{% if doc.project_name%}{{doc.project_name}}{% endif %}</td>
                </tr>
                <tr><td><b>Team</b></td>
                     <td>{% if doc.employee_group%}{{doc.employee_group}}{% endif %}</td>
                     <td><b>Department</b></td>
                     <td>{% if doc.department%}{{doc.department}}{% endif %}</td>
                </tr>
                <tr><td><b>Team Lead</b></td>
                    <td>{% if doc.project_lead_name%}{{doc.project_lead_name}}{% endif %}</td>
                    <td><b>Primary Consultant</b></td>
                     <td>{% if doc.primary_consultant_name%}{{doc.primary_consultant_name}}{% endif %}</td> 
                </tr>
                <tr><td><b>Contact No</b></td>
                     <td>{% if lead.cell_number%}{{lead.cell_number}}{% endif %}</td>
                      <td><b>Contact No</b></td>
                     <td>{% if emp.cell_number%}{{emp.cell_number}}{% endif %}</td>
                </tr>
                <tr><td><b>Contact Email</b></td>
                     <td>{% if lead.company_email%}{{lead.company_email}}{% endif %}</td>
                     <td><b>Conatct Email</b></td>
                    <td>{% if emp.company_email%}{{emp.company_email}}{% endif %}</td>
                </tr>


        </table>
       <br>
       {% set vars = {'a': 0} %}
         {% for i in task %}

        {% set tasklist = frappe.get_doc('Task',i.name)%}

        {% for row in tasklist.checklist_items %}
        {% if vars.update({'a': vars.a + 1}) %}{% endif %}
        {%endfor%}
        {%endfor%}
        <table style="width:100%;border:1px solid black;">
            <tr style="border-top:none;">
                <th style="padding-left:10px;padding-right:10px;color:black;
                            padding-top:2px !important;
                            padding-bottom:2px !important;
                            border-top:none;
                            border-left:1px solid black;
                            border-bottom:1px solid black;
                            font-size:12px;
                            font-style:bold;">Task Details</th>
                <th style="color:black;
                            padding:2px !important;
                            border-top:none;
                            border-left:1px solid black;
                            border-bottom:1px solid black;
                            font-size:12px;
                            font-style:bold;">Status</th>
                {%if not doc.flags.print_settings.hide_group_tasks%}
                <th style="color:black;
                            padding:2px !important;
                            border-top:none;
                            border-left:1px solid black;
                            border-bottom:1px solid black;
                            font-size:12px;
                            font-style:bold;">Parent Task</th>
                {%endif%}
                <th style="color:black;
                            padding:2px !important;
                            border-top:none;
                            border-left:1px solid black;
                            border-bottom:1px solid black;
                            font-size:12px;
                            font-style:bold;">Actual / Planned Hrs</th>
                <th style="color:black;
                            padding:2px !important;
                            border-top:none;
                            border-left:1px solid black;
                            border-bottom:1px solid black;
                            font-size:12px;
                            font-style:bold;">Expected Start Date</th>
                <th style="color:black;
                            padding:2px !important;
                            border-top:none;
                            border-left:1px solid black;
                            border-bottom:1px solid black;
                            font-size:12px;
                            font-style:bold;">Expected End date</th>
                <th style="color:black;
                            padding:2px !important;
                            border-top:none;
                            border-left:1px solid black;
                            border-bottom:1px solid black;
                            font-size:12px;
                            font-style:bold;">Progress%</th>
                {%if vars.a>0%}
                <th style="color:black;
                            padding:2px !important;
                            border-top:none;
                            border-left:1px solid black;
                            border-bottom:1px solid black;
                            font-size:12px;
                            font-style:bold;">Checklist</th>
                <th style="color:black;
                            padding:2px !important;
                            border-top:none;
                            border-left:1px solid black;
                            border-bottom:1px solid black;
                            font-size:12px;
                            font-style:bold;">Expected End Date</th>
                <th style="color:black;
                            padding:2px !important;
                            border-top:none;
                            border-left:1px solid black;
                            border-bottom:1px solid black;
                            font-size:12px;
                            font-style:bold;">Hours</th>
                {%endif%}
            </tr>
             {% for i in task %}
                {%set emp%}{% endset %}
                {% set tasklist = frappe.get_doc('Task',i.name)%}
                <tr>

                    <td rowspan=" {{tasklist.checklist_items |len +1}}" style="margin-left:15px;margin-right:15px;"><b>{% if tasklist.name%}{{tasklist.name}}{% endif %}</b><br>
                    {% if tasklist.subject%}{{tasklist.subject}}{% endif %}</td>
                    <td rowspan=" {{tasklist.checklist_items |len+1}}">{% if tasklist.status%}{{tasklist.status}}{% endif %}</td>
                    {%if not doc.flags.print_settings.hide_group_tasks%}
                    <td rowspan=" {{tasklist.checklist_items |len+1}}">{% if tasklist.parent_task%}{{tasklist.parent_task}}{% endif %}</td>{% endif %}
                    <td rowspan=" {{tasklist.checklist_items |len+1}}" style="text-align:right">{{tasklist.get_formatted("actual_time")}}/{{tasklist.expected_time}}</td>

                    <td rowspan=" {{tasklist.checklist_items |len+1}}"> {% if tasklist.exp_start_date%}{{frappe.utils.formatdate(tasklist.exp_start_date,"dd-mm-yyyy")}}{% endif %}</td>
                    <td rowspan=" {{tasklist.checklist_items |len+1}}"> {% if tasklist.exp_end_date%}{{frappe.utils.formatdate(tasklist.exp_end_date,"dd-mm-yyyy")}}{% endif %}</td>
                    <td rowspan=" {{tasklist.checklist_items |len+1}}" style="text-align:right"> {% if tasklist.progress%}{{tasklist.get_formatted("progress")}}{% endif %}</td>
                    {%if vars.a>0%}
                    {%if not tasklist.checklist_items %}
                    <td></td>
                    <td></td>
                    <td></td>
                    {%endif%}
                    </tr>
                    {% for row in tasklist.checklist_items %}
                    <tr>
                    <td>{{row.particulars}}</td>
                    <td>{{frappe.utils.formatdate(row.expected_end_date,"dd-mm-yyyy")}}</td>
                    <td>{{'%0.2f'| format(row.hours/3600|float)}}</td>
                    </tr>{% endfor %}
                    {%endif%}


              {% endfor %}
        </table>
