css:
 .Heading {
            box-shadow:1px 1px black;
            border: 1px solid black;
            text-align: center;

        }
.Heading h1{

     font-family: 'Poppins', sans-serif;
            font-weight: 700;
}
    .pheading h1{
        font-family: 'Poppins', sans-serif;
            font-weight: 700;
    }

    .pheading {
         margin-top:10px;
        text-align: center;
    }

    .cards {
        text-align: center;
         margin-top:10px;
    }

    .card {
        box-shadow:1px 1px black;
        border: 1px solid black;
        padding: 5px;
        margin: 10px;
        width: 45%;
        display: inline-block;
        vertical-align: top;
         border-radius: 10px



    }

    .card h3{
        font-family: 'Poppins', sans-serif;
        font-weight: 100;
        font-size:12px;
        text-align: left;
        margin-top:5px;
          margin-bottom:5px;
    }

#c2 h3{
     margin-bottom:36px;
}

.small-cards{
    margin-top:10px
}
.s-card{
     box-shadow:1px 1px black;
